package even.java;

public class Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n=100, i=1;  
	        System.out.print("List of even numbers from 1 to 100: \n");  
	        while (i<=n)   
	        { 
	            if ( i%2 == 0 )
	            {
	                System.out.print(i + " ");
	            }
	            i++;
	        }
	    }  
	
	}


